mapper.select:
select DATE_FORMAT(addTime,'%Y-%m-%d') as addTime from add_point_department where 1=1
and quarter = #{param1}
GROUP BY DATE_FORMAT(addTime,'%Y-%m-%d') order by DATE_FORMAT(addTime,'%Y-%m-%d')

select checkId,sum(addPoint) as countDepartment from add_point_department
where 1=1

    and quarter = #{param1}



    and departmentId=#{param3}

GROUP BY checkId

select DATE_FORMAT(addTime,'%Y-%m-%d') as addTime,sum(addPoint) as countDepartment from add_point_department where 1=1
and quarter = #{param1}
GROUP BY DATE_FORMAT(addTime,'%Y-%m-%d')

select t.departmentId,sum(t.addPoint) as countDepartment from add_point_department t where 1=1
and t.quarter = #{param1}
GROUP BY t.departmentId

