mapper.select:
select DATE_FORMAT(departmentAddPointTime,'%Y-%m-%d') as departmentAddPointTime from departmentKpi
where 1=1

    and quarter = #{param1}


    and weekNum = #{param2}

GROUP BY DATE_FORMAT(departmentAddPointTime,'%Y-%m-%d')
order by DATE_FORMAT(departmentAddPointTime,'%Y-%m-%d')

select checkId,sum(departmentAddPoint) as countDepartment from departmentKpi
where 1=1

    and quarter = #{param1}


    and weekNum = #{param2}


    and departmentId=#{param3}

GROUP BY checkId

select DATE_FORMAT(departmentAddPointTime,'%Y-%m-%d') as departmentAddPointTime,sum(departmentAddPoint) as countDepartment from departmentKpi
where 1=1

    and quarter = #{param1}


    and weekNum = #{param2}

GROUP BY DATE_FORMAT(departmentAddPointTime,'%Y-%m-%d')

select t.departmentId,sum(t.departmentAddPoint) as countDepartment
from departmentKpi t
where 1=1

    and t.quarter = #{param1}


    and t.weekNum = #{param2}

GROUP BY t.departmentId
