mapper.select:
SELECT
*
FROM
advice
where
1=1

    and title = #{title}


    and isSee = #{isSee}


    and isCross = #{isCross}

order by orderTime

mapper.update:
update user set
  userName=#{userName},pwd=#{pwd},
  permissionDegree=#{permissionDegree},
  departmentId=#{departmentId},kpiSum=#{kpiSum}
where
  userId=#{userId}

update user set userId=#{userId}

    ,pwd=#{pwd}


    ,phone=#{phone}


    ,email=#{email}


    ,hometown=#{hometown}

where
userId=#{userId}

mapper.select:
SELECT * FROM user where permissionDegree in(2,3)
and userName like CONCAT('%',#{userName},'%')
and userId = #{userId}
and departmentId =#{departmentId}
order by kpiSum desc
